import React, { useState, useEffect } from 'react'
import Nav from './Components/Nav'
import Wrapper from './Components/Wrapper'
import WeatherContext from './Utils/WeatherContext'
import Footer from './Components/Footer'
import API from './Utils/API'
import moment from 'moment'


// use context 
// set the state for the current weather, put the value of the api

function App() {

    const [currentWeather, setCurrentWeather] = useState({
        name: "",
        temperature: "",
        humidity: "",
        Windspeed: "",
        UVindex: "",
    })

//   make the axios calls, then set the state of the weather, once thats done. 
// use the vales of the state to fill in the props of compenents i.e Weather

// practice back end
// save the weather data to a data base then call it back when a button is pressed

 useEffect(() => {
   getWeather()
 }, [])

function getWeather() {
    const currentDate = moment().format(" (dddd, MMMM Do YYYY)");
    API.getWeather('California').then((res) => {
        console.log(res.data.name)
        setCurrentWeather({
            ...currentWeather,
            name: res.data.name,
            temperature: "Temperature: " + res.main.temp + "°",
            humidity: "Humidity" + res.main.humidity + "%",
            Windspeed: "Windspeed" + res.wind.speed + "MPH"
        })

    })
    console.log("hi " + currentDate)
}


return (
<div>
<Nav />
<WeatherContext.Provider>
< Wrapper/>
</WeatherContext.Provider>
< Footer/>
</div>


)

}




export default App;
