  const context = useContext(WeatherContext);

  return (
    <div>
      <h3>Five Day Forcast</h3>
      {context.map((day, i) => (
        <>
          <p key={i}>{day.dt_txt.substring(0, 10)}</p>
          <p>Temperature {day.main.temp} Â°</p>
          <p>Humidity {day.main.humidity} %</p>
        </>
      ))}
    </div>
  );
}

export default Cards;
